  public static void solve() throws IOException{
      int n=sc.nextInt(), k=sc.nextInt();
      char[] a=sc.next().toCharArray();
      ArrayList<Integer> arrl=new ArrayList<Integer>();
      int score=0;
      int cnt=0;
      int i=0;
      while(i<n && a[i]=='L'){
          i++;
      }
      for(i=i;i<n;i++){
          if(a[i]=='L'){
              cnt++;
          }
          else if(cnt>0){
              arrl.add(cnt);
              cnt=0;
          }
      }
      cnt=0;
      for(i=0;i<n;i++){
          if(a[i]=='W'){ if(i>0 && a[i-1]=='W'){
              score+=2;
          }
          else score+=1; cnt++;}
      }
      Collections.sort(arrl);
      for(int arr:arrl){
          if(arr<=k){
              score+=2*arr+1;
              k-=arr;
              cnt+=arr;
          }
          else{
              score+=2*k;
              cnt+=k;
              k=0;
              break;
          }
      }
      k=Math.min(k, n-cnt);
      if(k>0){
          if(ckwin(a, n)) score+=2*k;
          else score+=2*k-1;
      }
      nl(score);
  }

  public static boolean ckwin(char[] a, int n){
      for(int i=0;i<n;i++) if(a[i]=='W') return true;
      return false;
  }


  static long gcd(long a, long b)
	{
	if (b == 0)
		return a;
	return gcd(b, a % b);
  }

}

