            for (int i = 0; i < n; i++) {
                a[i] = (s.charAt(i) == 'W' ? 1 : 0);
                if (a[i] == 0) ct++;
                if (won && a[i] == 1) score += 2;
                else if (a[i] == 1) score += 1;
                won = a[i] == 1;
            }
            int run = 0;
            ArrayList<Integer> r = new ArrayList<>();
            for (int i = 0; i < n; i++) {
                if (a[i] == 0) {
                    run++;
                } else {
                    if (run != 0) r.add(run);
                    run = 0;
                }
            }
            if (run != 0) r.add(run);
            ArrayList<Integer> l = new ArrayList<>(); int sides = 0;
            for (int i = 0; i < r.size(); i++) {
                if (i == 0 && a[0] == 0){
                    sides += r.get(i);
                    continue;
                }
                if (i == r.size() - 1 && a[n - 1] == 0) {
                    sides += r.get(i);
                    continue;
                }
                l.add(r.get(i));
            }
            Collections.sort(l); int cur = 0;
            int delta = 0;
            while (k > 0 && cur < l.size()) {
                int here = l.get(cur);
                if (k < here) {
                    delta += 2 * k;
                } else {
                    delta += 2 * (here - 1);
                    delta += 3;
                }
                k -= Math.min(k, here);
                ++cur;
            }
            if (k > 0) {
                delta += Math.min(k * 2, sides * 2);
            }
            if (ct == n) {
                delta = 0;
                if (k == 0) out.println(score);
                else out.println(score + (k - 1) * 2 + 1);
            } else out.println(score + delta);
        }
        out.close();
