import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class Solution {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // System.out.println(solve(5, "LLLWWL"));

        //  System.exit(0);
        int t = sc.nextInt();
        for (int tc = 0; tc < t; tc++) {
            int n = sc.nextInt();
            int k = sc.nextInt();

            String s = sc.next();

            System.out.println(solve(k, s));
        }
    }

    private static int solve(int k, String s) {
        /*
        cnt(W) * 2 - 1 - gaps
         */

        /*
        LLWLLWLLL k = 2
         */
        int cntW = 0;
        List<Integer> gapLen = new ArrayList<>();
        int gapPos = -1;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == 'L' && i > 0 && s.charAt(i - 1) == 'W') {
                gapPos = i;
            }

            if (s.charAt(i) == 'W') {
                cntW++;

                if (gapPos != -1) {
                    gapLen.add(i - gapPos);
                    gapPos = -1;
                }
            }
        }

        if (cntW + k > s.length()) {
            return s.length() * 2 - 1;
        }

        gapLen.sort(Integer::compare);

        int solvedGaps = 0;
        for (int i = 0; i < gapLen.size(); i++) {
            if (gapLen.get(i) <= k) {
                solvedGaps++;
                k -= gapLen.get(i);
                cntW += gapLen.get(i);
            } else {
                break;
            }
        }

        int res = (cntW + k) * 2 - 1 - gapLen.size() + solvedGaps;
        return res < 0 ? 0 : res;
    }

}

