                if i < l and sg[i] == 'W':
                    gaps.append(lcnt)
                else:
                    temp += lcnt

    gaps.sort()
    gap_l = len(gaps)
    j = 0
    while  k > 0 and j < gap_l:
        if gaps[j]  <= k:
            k -= gaps[j]
            filled_gaps += 1
        j += 1
    if k > 0 and temp > 0 and wins == 0:
        win_streaks += 1

    score = 2*(min(wins + ik , l)) - win_streaks + filled_gaps
    print(score)

