    static class Solver {
        public void solve(InputReader sc, PrintWriter out) {
            int n = sc.nextInt();
            int k = sc.nextInt();
            char[] arr = sc.next().toCharArray();
            int fidx = n;
            int lidx = -1;
            int total = 0;
            for(int i = 0; i < n; i++) {
                if(arr[i]=='W') {
                    total++;
                    if (i > 0 && arr[i - 1] == 'W') total++;
                    if (fidx == n) fidx = i;
                    lidx = i;
//                    out.println(" HI " + i);
                }
            }
            if(fidx==n) {
                if(k==0) {
                    out.println(0);
                    return;
                }
                long ans = 1;
                k--;
                ans += (long)k*2;
                out.println(ans);
                return;
            }
            ArrayList<Integer> llengts = new ArrayList<>();
            int cnt = 0;
//            out.println(total);
            for(int i = fidx+1; i <= lidx; i++) {
                char now = arr[i];
                if(now=='L') {
                    cnt++;
                } else {
                    if(cnt>0) llengts.add(cnt);
                    cnt = 0;
                }
            }
            Collections.sort(llengts);
//            out.println(llengts + " " + fidx + " " + lidx + " " + total);
            for(int i = 0; i < llengts.size();i++) {
                if(k<1) break;
                long now = llengts.get(i);
                if(now<=k) {
                    total+=now*2+1;
                    k-=now;
                } else {
                    total += k*2;
                    k = 0;
                }
            }
            if(k>0) {
                if(fidx>=1) {
                    total+=Math.min(fidx,k)*2;
                    k-= Math.min(fidx,k);
                }
                if(k>0) {
                    long right = n-lidx-1;
                    total+=Math.min(k,right)*2;
                }
            }
            out.println(total);
        }
    }

