import java.io.*;
import java.util.ArrayList;
import java.util.Comparator;
import java.util.List;
import java.util.StringTokenizer;

public class B {
    private FastScanner in;
    private PrintWriter out;

    public B(FastScanner in, PrintWriter out) {
        this.in = in;
        this.out = out;
    }

    public static void main(String[] args) {
        try (
                FastScanner in = new FastScanner(System.in);
                PrintWriter out = new PrintWriter(System.out);
        ) {
            B solution = new B(in, out);
            solution.solve();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void solve() throws IOException {
        int t = in.nextInt();
        while (t-- > 0) {
            int n = in.nextInt();
            int k = in.nextInt();
            String gameHistory = in.next();
            List<Integer> loseSegments = new ArrayList<>();
            int start = -1;
            int res = 0;
            int reserve = 0;
            for (int i = 0; i < gameHistory.length(); i++) {
                char c = gameHistory.charAt(i);
                if (c == 'L') {
                    if (start < 0) {
                        start = i;
                    }
                    reserve++;
                } else {
                    if (start >= 0) {
                        loseSegments.add(i - start);
                        start = -1;
                        res++;
                    } else {
                        res += 2;
                    }
                }
            }
            if (start > -1) {
                loseSegments.add(gameHistory.length() - start);
            }
            start = 0;
            int end = loseSegments.size();
            if (gameHistory.charAt(0) == 'L') {
                start++;
            } else {
                res--;
            }
            if (gameHistory.charAt(gameHistory.length() - 1) == 'L') {
                end--;
            }
            if (start < end) {
                List<Integer> toKill = loseSegments.subList(start, end);
                toKill.sort(Comparator.naturalOrder());
                for (int segment : toKill) {
                    if (segment > k) {
                        break;
                    }
                    k -= segment;
                    res++;
                    res += segment * 2;
                    reserve -= segment;
                }
            }
            if (reserve == n && k > 0) {
                res--;
            }
            res += Math.min(k, reserve) * 2;
            out.println(res);
        }
    }


    public static class FastScanner implements Closeable {
        BufferedReader bufferedReader;
        StringTokenizer stringTokenizer;

        public FastScanner(InputStream inputStream) {
            this.bufferedReader = new BufferedReader(new InputStreamReader(inputStream));
        }

        public FastScanner(File file) throws FileNotFoundException {
            this(new FileInputStream(file));
        }

        public String next() throws IOException {
            while (stringTokenizer == null || !stringTokenizer.hasMoreTokens()) {
                String line = bufferedReader.readLine();
                if (line == null) {
                    throw new EOFException("End of input stream is reached.");
                }
                stringTokenizer = new StringTokenizer(line);
            }
            return stringTokenizer.nextToken();
        }

        public String nextLine() throws IOException {
            stringTokenizer = null;
            return bufferedReader.readLine();
        }

        public int nextInt() throws IOException {
            return Integer.parseInt(next());
        }

        public long nextLong() throws IOException {
            return Long.parseLong(next());
        }

        public double nextDouble() throws IOException {
            return Double.parseDouble(next());
        }

        public int[] nextIntArray(int n) throws IOException {
            int[] res = new int[n];
            for (int i = 0; i < n; i++) {
                res[i] = nextInt();
            }
            return res;
        }

        public List<Integer> nextIntList(int n) throws IOException {
            List<Integer> res = new ArrayList<>(n);
            for (int i = 0; i < n; i++) {
                res.add(nextInt());
            }
            return res;
        }

        @Override
        public void close() throws IOException {
            bufferedReader.close();
        }
    }
}

