        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int T = Integer.parseInt(st.nextToken());

        for(int t = 0; t < T; t++){
            st = new StringTokenizer(br.readLine());
            int N = Integer.parseInt(st.nextToken());
            int K = Integer.parseInt(st.nextToken());
            String S = br.readLine();

            int score = 0;
            for(int i = 0; i < N; i++){
                if(S.charAt(i) == 'W'){
                    if(i == 0 || S.charAt(i-1) == 'L')
                        score++;
                    else
                        score += 2;
                }
            }

            int pre = 0;
            while(pre < N && S.charAt(pre) == 'L')
                pre++;

            int suf = 0;
            while(suf < N && S.charAt(N-suf-1) == 'L')
                suf++;

            if(pre == N && suf == N){
                System.out.println(Math.max(0, 2*K-1)); continue;
            }

            ArrayList<Integer> lengths = new ArrayList<>();
            int len = 0;
            for(int i = pre; i < N-suf; i++){
                if(S.charAt(i) == 'W'){
                    if(len != 0)
                        lengths.add(len);
                    len = 0;
                } else len++;
            }

            Collections.sort(lengths);

            for(int x : lengths){
                if(K >= x){
                    score += 2*x+1;
                    K -= x;
                } else {
                    score += 2*K;
                    K = 0;
                }
            }

            if(K > 0 && pre > 0){
                int temp = Math.min(K, pre);
                score += 2 * temp;
                K -= temp;
            }

            if(K > 0 && suf > 0){
                int temp = Math.min(K, suf);
                score += 2 * temp;
                K -= temp;
            }

            System.out.println(score);
        }
    }
}

