def get_close(n, i, res):
    while i < n and res[i] == "L":
        i += 1
    return i - 1

def calc(n, res):
    count = 0
    for i in range(n):
        if i > 0 and res[i-1] == res[i] == "W":
            count += 2
        elif res[i] == "W":
            count += 1
    return count

def solve(n, k, res):
    gaps = []
    rem = []

    i = 0
    while i < n:
        if res[i] == "L":
            op, cl = i,  get_close(n, i+1, res)
            gaps.append((cl-op+1, op, cl))
            i = cl + 1

            if op == 0 or cl == n-1:
                rem.append(gaps.pop())
        else:
            i += 1

    gaps.sort()
    for gap, start, stop in gaps:
        if not k:
            break
        for i in range(start, stop+1):
            if k:
                res[i] = "W"
                k -= 1
            else:
                break

    for _, start, stop in rem:
        if start == 0:
            g = reversed(range(start, stop+1))
        else:
            g = range(start, stop+1)

        for i in g:
            if k:
                res[i] = "W"
                k -= 1
            else:
                break

    return calc(n, res)


