public class B1427 {

	public static void main(String[] args) throws IOException {
		Scanner sc = new Scanner(System.in);
		PrintWriter pw = new PrintWriter(System.out);
		int t = sc.nextInt();
		while (t-- > 0) {
			int n = sc.nextInt();
			int k = sc.nextInt();
			char[] arr = sc.next().toCharArray();
			int prevW = -1;
			ArrayList<Integer> al = new ArrayList<Integer>();
			int cntW = 0;
			for (int i = 0; i < arr.length; i++) {
				if (arr[i] == 'W') {
					cntW++;
					if (prevW != i - 1 && prevW != -1) {
						al.add(i - prevW - 1);
					}
					prevW = i;
				}
			}
			int ogk = k;
			int comps = al.size() + 1;
			Collections.sort(al);
			for (int x : al) {
				if (k >= x) {
					k -= x;
					comps--;
				} else {
					break;
				}
			}
//			System.out.println(al);
			cntW = Math.min(cntW + ogk, arr.length);
			pw.println(Math.max(2 * cntW - comps, 0));
		}
		pw.close();

	}

	static class Scanner {
		BufferedReader br;
		StringTokenizer st;

		public Scanner(InputStream s) {
			br = new BufferedReader(new InputStreamReader(s));
		}

		public Scanner(FileReader f) {
			br = new BufferedReader(f);
		}

		public String next() throws IOException {
			while (st == null || !st.hasMoreTokens())
				st = new StringTokenizer(br.readLine());
			return st.nextToken();
		}

		public int nextInt() throws IOException {
			return Integer.parseInt(next());
		}

		public long nextLong() throws IOException {
			return Long.parseLong(next());
		}

		public double nextDouble() throws IOException {
			return Double.parseDouble(next());
		}

		public int[] nextIntArr(int n) throws IOException {
			int[] arr = new int[n];
			for (int i = 0; i < n; i++) {
				arr[i] = Integer.parseInt(next());
			}
			return arr;
		}

	}

}

import java.util.Scanner;
import java.util.PriorityQueue;

public class Solution {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int t = sc.nextInt();
        for (int tc = 0; tc < t; tc++) {
            int n = sc.nextInt();
            int k = sc.nextInt();

            String s = sc.next();

            System.out.println(solve(k, s));
        }
    }

    private static int solve(int k, String s) {
        /*
        cnt(W) * 2 - 1 - gaps
         */

        /*
        LLWLLWLLL k = 2
         */
        int cntW = 0;
        PriorityQueue<Integer> gapLen = new PriorityQueue<>();
        int gapPos = -1;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == 'L' && i > 0 && s.charAt(i - 1) == 'W') {
                gapPos = i;
            }

            if (s.charAt(i) == 'W') {
                cntW++;

                if (gapPos != -1) {
                    gapLen.add(i - gapPos);
                    gapPos = -1;
                }
            }
        }

        if (cntW + k > s.length()) {
            return s.length() * 2 - 1;
        }

        while (gapLen.size() > 0 && gapLen.peek() <= k) {
            Integer gap = gapLen.remove();
            k -= gap;
            cntW += gap;
        }

        int res = (cntW + k) * 2 - 1 - gapLen.size();
        return res < 0 ? 0 : res;
    }

}

