  private static void solve() {
    int n = rInt(), k = rInt();
    char[] s = rStr().toCharArray();
    long w = 0;
    for (char c : s) {
      w += (c == 'W' ? 1 : 0);
    }
    if (k > 0 && w == 0) {
      k--;
      s[0] = 'W';
    }
    Map<Integer, Integer> loseStk = new HashMap<>();
    int lastW = -1;
    for (int i = 0; i < n; i++) {
      if (s[i] == 'W') {
        if (lastW < i - 1) {
          loseStk.put(lastW + 1, i - lastW - 1);
        }
        lastW = i;
      }
    }
    //remove streaks next to the walls
    loseStk.remove(0);
    ArrayList<Integer> streaks = new ArrayList<>(loseStk.values());
    Collections.sort(streaks);
    Queue<Integer> queue = new ArrayDeque<>(streaks);
    int cheated = 0;
    while (k > 0 && !queue.isEmpty()) {
      int streak = queue.remove();
      if (streak <= k) {
        cheated += 2 * streak + 1;
      } else {
        cheated += 2 * k;
      }
      w += Math.min(k, streak);
      k -= Math.min(k, streak);
    }
    cheated += (2 * Math.min(k, n - w));
    int score = cheated;
    int streak = 0;
    for (int i = 0; i < n; i++) {
      if (s[i] == 'W') {
        score += (streak > 0) ? 2 : 1;
        streak++;
      } else {
        streak = 0;
      }
    }
    wl(score);
  }

