	public static void main(String[] args) {
		Scanner sc=new Scanner(System.in);
		int t=sc.nextInt();
		for(int i=0;i<t;i++) {
			int n=sc.nextInt();
			int k=sc.nextInt();
			sc.nextLine();
			String s=sc.nextLine();
			int p=0;
			int q=n-1;
			List<Integer> l=new LinkedList<>();
			while((!String.valueOf(s.charAt(p)).equals("W") || !String.valueOf(s.charAt(q)).equals("W")) && (p<q)) {
				if(!String.valueOf(s.charAt(p)).equals("W")) p++;
				if(!String.valueOf(s.charAt(q)).equals("W")) q--;
			}
			if(p>=q) {
				if(p==q && String.valueOf(s.charAt(p)).equals("W")) {
					System.out.println(2*Math.min(k,p+n-q-1)+1);
				}
				else {
					if(k==0) {
						System.out.println("0");
					}
					else {
						System.out.println(2*(Math.min(k,n)-1)+1);
					}
				}
			}
			else {
				int size=0;
				int sum=0;
				for(int j=p;j<=q;j++) {
					if(String.valueOf(s.charAt(j)).equals("W")) {
						if(j!=0 && String.valueOf(s.charAt(j-1)).equals("W")) sum+=2;
						else sum+=1;
						if(size==0) continue;
						else {
							l.add(size);
							size=0;
						}
					}
					else {
						size+=1;
					}
				}
				Collections.sort(l);
				int extra=0;
				while(k!=0 && l.size()!=0) {
					if(k<l.get(0)) {
						extra+=(2*k);
						k-=k;
					}
					else {
						extra+=((2*l.get(0))+1);
						k-=l.remove(0);
					}
				}
				if(k!=0) {
					if(p+n-q-1>k) extra+=(2*k);
					else extra+=((2*(p+n-q-1)));
					System.out.println(extra+sum);
				}
				else {
					System.out.println(sum+extra);
				}
			}
		}
	}
}

