		StringBuilder sb = new StringBuilder();
		for(; q > 0; q--) {
			StringTokenizer st = new StringTokenizer(in.readLine());
			int n = pint(st), k = pint(st);
			String str = in.readLine();
			boolean last = false;
			int p = 0;
			int s = 0;
			PriorityQueue<Integer> pq = new PriorityQueue<Integer>();
			for(int i = 0 ; i < n; i++) {
				if(str.charAt(i) == 'W') {
					if(s != 0) {
						pq.add(s); s = 0;
					}
					if(last) {p += 2;}
					else {p++;}
					last = true;
				}else {
					last = false;
					s++;
				}
			}
			if(s == n) {
				sb.append(Math.max(2 * k - 1,0));
				sb.append('\n');
				continue;
			}
			int t = 0;
			for(int i = 0; i < n; i++) {
				if(str.charAt(i) == 'W') {break;}
				t++;
			}
			while(!pq.isEmpty()) {
				int c = pq.poll();
				if(c <= k) {
					if(c == t) {
						t = -t;
						continue;
					}else {
						p += 2 * c + 1;
						k -= c;
					}
				}else {
					p += 2 * k;
					k = 0;
					break;
				}
			}
			if(t < 0) {t = -t;}
			if(k > 0) {
				p +=  2 * Math.min(k, t);
				k -= t;
				if(k > 0) {
					p += 2 * Math.min(k, s);
				}
			}
			sb.append(p);
			sb.append('\n');
		}
		System.out.print(sb);
	}
