  for (int i = 0; i < n; i++) {
    if (s[i] == 'W') {
      int j = i + 1;
      while (j < n && s[j] == 'L') j++;
      if (j == n) {
        pos = i + 1;
        break;
      }
      mp[j - i - 1].push_back(i + 1);
      i = j - 1;
    }
  }
  for (it = mp.begin(); it != mp.end(); it++) {
    vector<int> a = it->second;
    for (int i = 0; i < a.size(); i++) {
      for (int j = 0; j < it->first; j++) {
