using namespace std;
int read() {
  char c;
  int num, f = 1;
  while (c = getchar(), !isdigit(c))
    if (c == '-') f = -1;
  num = c - '0';
  while (c = getchar(), isdigit(c)) num = num * 10 + c - '0';
  return num * f;
}
int n, k, cnt, a[100009], tot, ans;
char c[100009];
void work() {
  n = read();
  k = read();
  cnt = 0;
  tot = 0;
  ans = 0;
  scanf("%s", c + 1);
  for (int i = 1; i <= n; i++) cnt += c[i] == 'L';
  for (int i = 1; i <= n; i++) {
    if (c[i] == 'W') {
      if (i == 1 || c[i - 1] == 'L')
        ans++;
      else
        ans += 2;
    }
  }
  if (k == 0) {
    printf("%d\n", ans);
    return;
  }
  if (k > cnt) {
    printf("%d\n", 2 * n - 1);
    return;
  }
  if (cnt == n) {
    printf("%d\n", 2 * k - 1);
    return;
  }
  int now = 1;
  cnt = 0;
  while (c[now] != 'W') now++;
  for (; now <= n; now++) {
    if (c[now] == 'W') {
      if (cnt > 0) a[++tot] = cnt;
      cnt = 0;
    } else {
      cnt++;
    }
  }
  sort(a + 1, a + 1 + tot);
  for (int i = 1; i <= tot; i++) {
    if (k < a[i]) break;
    k -= a[i];
    ans += a[i] * 2 + 1;
  }
  if (k) ans += 2 * k;
  printf("%d\n", ans);
}
int main() {
  int Case = read();
  while (Case--) work();
  return 0;
}

import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Scanner;
import java.util.Collections;
import java.util.ArrayList;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        Scanner in = new Scanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskB solver = new TaskB();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskB {
        public void solve(int testNumber, Scanner in, PrintWriter out) {
            int t = in.nextInt();
            while (t-- > 0) {
                int ans = 0;
                int n = in.nextInt();
                int k = in.nextInt();
                String str = in.next();
                int losses = 0;
                int opt = 0;
                ArrayList<Integer> rest = new ArrayList<>();
                int start = 0, end = 0;
                int count = 0;
                for (int i = 0; i < n; i++) {
                    if (i == 0 && str.charAt(i) == 'W') {
                        ans++;
                        continue;
                    }
                    if (str.charAt(i) == 'W')
                        if (str.charAt(i - 1) == 'W')
                            ans += 2;
                        else
                            ans++;
                }
                int flag = 0;
                for (int i = 0; i < n; i++) {
                    if (str.charAt(i) == 'L') {
                        losses++;
                        end++;
                        if (flag == 0)
                            start++;
                    }
                    if (i != 0 && i != n - 1) {
                        if (str.charAt(i - 1) == 'W' && str.charAt(i + 1) == 'W' && str.charAt(i) == 'L') {
                            opt++;
                            continue;
                        }
                    }
                    if (flag == 1) {
                        if (str.charAt(i) == 'W' && count != 0 && flag == 1) {
                            rest.add(count);
                            count = 0;
                        } else if (str.charAt(i) == 'L')
                            count++;
                    }
                    if (str.charAt(i) == 'W') {
                        flag = 1;
                        end = 0;
                    }
                }
                Collections.sort(rest);
                if (k >= opt) {
                    ans += 3 * opt;
                    k -= opt;
                } else {
                    ans += 3 * k;
                    k = 0;
                }
                for (int i = 0; i < rest.size(); i++) {
                    int length = rest.get(i);
                    if (length <= k) {
                        ans += 2 * length + 1;
                        k -= length;
                    } else {
                        ans += 2 * k;
                        k = 0;
                    }
                }
                if (k == 0) {
                    out.println(ans);
                    continue;
                }
                if (flag == 0) {
                    if (k >= n)
                        ans += 1 + 2 * (n - 1);
                    else
                        ans += 1 + 2 * (k - 1);
                } else {
                    if (k >= end) {
                        ans += 2 * end;
                        k -= end;
                    } else {
                        ans += 2 * k;
                        k = 0;
                    }
                    if (k >= start) {
                        ans += 2 * start;
                        k -= start;
                    } else {
                        ans += 2 * k;
                        k = 0;
                    }
                }
                out.println(ans);
            }
        }

    }
}

