public class A {
	static FastReader f = new FastReader();
	static PrintWriter out = new PrintWriter(System.out);

	public static void main(String[] args) {
		int t = f.nextInt();
		while(t-- > 0)
			solve();
		out.close();
	}

	static void solve() {
		int n = f.nextInt(), k = f.nextInt();

		char[] s = f.next().toCharArray();
		PriorityQueue<Pair> pairs = new PriorityQueue<>();
		int last = -1;
		int first = -1;
		for(int i=0;i<n;i++) {
			if(s[i] == 'W') {
				if(last != -1) {
					if(last != first - 1) {
						Pair p = new Pair(last, i);
						pairs.add(p);
						//System.out.println("deb "+p.start+" "+p.end);
					}
				} else {
					first = i;
				}
				last = i;
			}
		}

		if(last == -1) {
			out.println(k==0? 0 : Math.min(n,k) * 2 - 1);
			return;
		} 

		while(k > 0 && !pairs.isEmpty()) {
			Pair now = pairs.poll();
			//System.out.println("deb "+now.start+" "+now.end);
			for(int i = now.start + 1;k>0 && i<now.end;i++) {
				s[i] = 'W';
				k--;
			}
		}

		first--;
		for(int i=first;k>0 && i>=0;i--) {
			k--;
			s[i] = 'W';
		}

		last++;
		for(int i=last;k>0&&i<n;i++) {
			s[i] = 'W';
			k--;
		}

		long ans = 0;
		for(int i=0;i<n;i++) {
			if(s[i] == 'W') {
				if(i > 0 && s[i-1] == 'W') {
					ans+=2;
				} else {
					ans++;
				}
			}
		}



		out.println(ans);

	}

	static class Pair implements Comparable<Pair> {
		int start, end;
		Pair(int start, int end) {
			this.start = start;
			this.end = end;
		}

		public int compareTo(Pair o) {
			int size = end - start;
			int oSize = o.end - o.start;
			if(size == oSize) {
				return start - o.start;
			}
			return size - oSize;
		}
	}


	static class FastReader {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		StringTokenizer st;

		String next() {
			while(st == null || !st.hasMoreElements()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch(IOException ioe) {
					ioe.printStackTrace();
				}
			}
			return st.nextToken();
		}

		String nextLine() {
			String s = "";
			try {
				s = br.readLine();
			} catch(IOException ioe) {
				ioe.printStackTrace();
			}

			return s;
		}

		int  nextInt() {
			return Integer.parseInt(next());
		}

		long  nextLong() {
			return Long.parseLong(next());
		}
	}
}

