public class Main {
    public static void main(String[]args){
        Scanner in=new Scanner(System.in);
        while(in.hasNext()){
            int t=in.nextInt();
            while(t-->0){

            int n=in.nextInt();
            int k=in.nextInt();
            int i=0,j=n-1;
            char[]ch=(in.next()+"*").toCharArray();
            int l=0;
            int r=0;
            while(ch[i]=='L'){
                l++;
                i++;
            }
            while(ch[j]=='L'&&i<j){
                r++;
                j--;
            }
            if(l+r==n){
                if(k==0)System.out.println(0);
                else System.out.println(k*2-1);
            }
            else{
                PriorityQueue<Integer>que=new PriorityQueue<Integer>();
                char pre='L';
                int cnt=0;
                long ans=0;
                for(int a=i;a<=j;a++){
                    if(pre=='L'){
                        if(ch[a]=='L'){
                            cnt++;
                        }else{
                            que.add(cnt);
                            cnt=0;
                            ans+=1;
                        }
                    }else{
                        if(ch[a]=='L'){
                            cnt++;
                        }else{
                            ans+=2;
                        }
                    }
                    pre=ch[a];
                }
                que.poll();
                while(!que.isEmpty()){
                    int a=que.poll();
                    if(a<=k){
                        k-=a;
                        ans+=a*2+1;
