public class Chess {
    public static void main(String[] args) throws IOException {
        //read
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] s1;
        char[] s2;
        int t;
        t = Integer.parseInt(br.readLine());
        int[] res = new int[t];
        for (int j = 0; j < t; j++) {
            int n,k;
            s1 = br.readLine().split(" ");
            s2 = br.readLine().toCharArray();
            n = Integer.parseInt(s1[0]);
            k = Integer.parseInt(s1[1]);
            res = Score(n,k,s2,res,j);
        }
        for (int i = 0; i < t; i++) {
            System.out.println(res[i]);
        }

    }
    public static int[] Score(int n,int k,char [] s2,int[] res,int j){
        int score = 0;
        int l = 0;
        int[] s = new int[n];
        int tag = 0;
        Stack<Integer> stack = new Stack<Integer>();
        boolean flag = true;
        if(s2[0] == 'W'){
            score  = 1;
            flag = false;
        }else {
            l++;
        }
        for (int i = 1; i < n; i++) {
            if (s2[i] == 'W'){
                if(s2[i-1] == 'W'){
                    score += 2;
                }else{
                    score += 1;
                    stack.push(l);
                }
            }else{
                l++;
            }
        }
        if(stack.isEmpty()){
            if(score>0) {
                res[j] = score + 2 * Math.min(k,l);
                return res;
            }
            else{
                if (k == 0){
                    res[j] = 0;
                    return res;
                }
                res[j] = 2 * k - 1;
                return res;
            }
        }

        int[] L = new int[stack.size()];
        if(k>=l){
            res[j] = 2*n-1;
            return res;
        }else{
            for (int i = stack.size()-1; i > 0 ; i--) {
                L[i] = stack.pop()-stack.peek();
            }
            L[0] = stack.peek();
        }
        if(flag){
            tag = stack.peek();
        }
        Arrays.sort(L);
        for (int i : L) {
            if(i == 0){
                continue;
            }
            if(k>0){
                if(tag == i && flag){
                    flag = false;
                    continue;
                }
                if(k >= i){
                    score = score + 2*i+1;
                }else{
                    score = score + 2*k;
                }
                k -= i;
            }
            else{
                break;
            }
        }
        if(k>0){
            score = score + 2*k;
        }
        res[j] = score;
        return res;
    }
}

