		}
		int mult = Math.min(n - list.size(), k);
		if(ans == 0) ans += (mult - 1)  * 2 + 1;
		else ans += (mult) * 2;
		int size = list.size();
		List<Integer> l = new ArrayList<>();
		for(int i = 0; i + 1 < size; i++) {
			int gap = list.get(i + 1) - list.get(i);
			if(gap == 1) continue;
			l.add(gap);
		}
		Collections.sort(l);
		for(int i : l) {
			if(i <= k + 1){
				ans++;
				k -= i;
				k++;
			}
		}
		ans = Math.max(0, ans);
		sb.append(ans);
		sb.append("\n");
	}
}

