def makeRepeat(arr):
    repeat = [[arr[0], 0]]
    for x in arr:
        if x == repeat[-1][0]:
            repeat[-1][1] += 1
        else:
            repeat.append([x, 1])
    return repeat


def solve(N, K, S):
    if all(x == "L" for x in S):
        if K:
            return (min(N, K) - 1) * 2 + 1
        else:
            return 0
    repeats = makeRepeat(S)
    firstStreak = 0
    lastStreak = 0
    if repeats[-1][0] == "L":
        lastStreak = repeats.pop()[1]

    if repeats and repeats[0][0] == "L":
        firstStreak = repeats[0][1]
        repeats = repeats[1:]

    base = 0
    streaks = []
    for ch, repeat in repeats:
        if ch == "L":
            streaks.append(repeat)
        else:
            base += (repeat - 1) * 2 + 1
    streaks.sort()

    # print(S, K, base)
    # print(streaks, lastStreak)

    total = 0
    for x in streaks:
        if x <= K:
            total += x * 2 + 1
            K -= x
        else:
            x = min(K, x)
            total += x * 2
            K -= x
    # print("after", total, K, firstStreak, lastStreak)

    if K and lastStreak:
        x = min(K, lastStreak)
        total += x * 2
        K -= x

    if K and firstStreak:
        x = min(K, firstStreak)
        total += x * 2
        K -= x

    return base + total


