void solve() {
  long long n, k;
  cin >> n >> k;
  string second;
  cin >> second;
  long long cnt = 0;
  for (auto u : second) cnt += (u == 'W');
  if (cnt == 0) {
    long long ans = 2 * k - 1;
    cout << max(0LL, ans) << "\n";
    return;
  }
  vector<long long> idx;
  long long i = 0;
  while (i < n and second[i] == 'L') i++;
  while (i < n) {
    long long j = i;
    while (j < n and second[j] == 'W') j++;
    long long tmp = j;
    while (j < n and second[j] == 'L') j++;
    if (j < n) idx.push_back(j - tmp);
    i = j;
  }
  if (cnt + k >= n) {
    cout << 2 * n - 1 << "\n";
    return;
  }
  long long ans = cnt;
  for (long long i = 1; i < n; ++i) {
    if (second[i] == 'W' and second[i - 1] == 'W') ans++;
  }
  sort(idx.begin(), idx.end());
  for (auto u : idx) {
    if (k >= u) {
      ans += 2 * u;
      ans++;
      k -= u;
    } else if (k != 0) {
      ans += 2 * k;
      k = 0;
    }
  }
  if (k) ans += 2 * k;
  cout << ans << "\n";
}
