    if (a == 'W') {
      if (loss) {
        v.push_back(loss);
        loss = 0;
      }
      ans++;
      if (W) ans++;
      W = 1;
      win++;
    } else {
      W = 0;
      if (win) loss++;
    }
  }
  if (win == 0) {
    cout << max(2 * k - 1, 0LL) << endl;
  } else if (win + k >= n)
    cout << 2 * n - 1 << endl;
  else {
    sort(v.begin(), v.end());
    ans += 2 * k;
    for (auto x : v) {
      if (x <= k) ans++;
      k -= x;
    }
    cout << ans << endl;
  }
}
