def solve(n, k, s):
    gaps = []
    first_w_index = -1
    last_w_index = -1
    score = 0
    win_streak = 0

    for i in range(n):
        if s[i] == 'W':
            win_streak += 1
            if first_w_index == -1:
                first_w_index = i
            elif i - last_w_index - 1 > 0:
                gaps.append(i-last_w_index-1)
            last_w_index = i
        else:
            if win_streak > 0:
                score += 2 * win_streak - 1
                win_streak = 0

    if win_streak > 0:
        score += 2*win_streak-1

    gaps.sort()
    rem = []
    for g in gaps:
        if g <= k:
            score += 2*g+1
            k -= g
        else:
            rem.append(g)

    if k > 0:
        if len(rem) > 0:
            score += 2*k
        else:  # all in-between gaps are filled
            # last_w_index >= first_w_index
            if first_w_index == -1:  # no win
                score += 2*k-1
            else:
                score += 2 * min(k, first_w_index + n-last_w_index-1)

    return score


