public class B {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        int t = in.nextInt();
        for (int tt = 0; tt < t; tt++) {
            int n = in.nextInt(), k = in.nextInt();
            char[] s = in.next().toCharArray();
            pw.println(solve(s, n, k));
        }

        pw.close();
    }

    static long solve(char[] arr, int n, int k) {
       int res = 0;
       int los = 0;
       if (n == k) return 2 * n - 1;
       for (int i = 0; i < n; i++) {
           if (arr[i] == 'W') {
               res++;
               if (i > 0 && arr[i - 1] == 'W') res++;
               continue;
           }
           los++;
       }
       if (k == 0) return res;
       if (los < k) return 2 * n - 1;
       if (los == n) return 2 * k - 1;

       res += 2 * k;
       int cnt = Integer.MIN_VALUE;
       PriorityQueue<Integer> diff = new PriorityQueue<>();
       for (int i = 0; i < n; i++) {
           if (arr[i] == 'W') {
               if (cnt > 0)diff.add(cnt);
               cnt = 0;
           } else cnt++;
       }
       while (!diff.isEmpty()) {
           int temp = diff.remove();
           if (temp > k) break;
           res++;
           k -= temp;
       }
       return res;
    }


