      else if (s[i] == 'W')
        ans++;
    }
    if (k == 0) {
      cout << ans << "\n";
    } else if (al != n) {
      k = min(k, al);
      for (auto &i : v) {
        if (i > k) break;
        k -= i;
        ans += i * 2 + 1;
      }
      cout << (ans + k * 2) << "\n";
    } else {
      k = min(k, al);
      cout << (k * 2 - 1) << "\n";
    }
  }
  return 0;
}

