      for (int i = 0; i <= n - 1; i++) {
        if (s[i] == 'W') {
          if (i == 0 || s[i - 1] == 'L') {
            wins_streaks++;
          }
          wins++;
        } else {
          losses++;
          if (i == 0 || s[i - 1] == 'W') {
            losing_streaks.push_back(0);
          }
          losing_streaks.back()++;
        }
      }
      if (k >= losses) {
        cout << 2 * n - 1 << endl;
        continue;
      }
      if (wins == 0) {
        if (k == 0) {
          cout << 0 << endl;
        } else {
