long long get_score(string s) {
  long long score = 0;
  bool streak = false;
  for (auto c : s) {
    if (c == 'L')
      streak = false;
    else {
      if (streak)
        score += 2;
      else {
        score++;
        streak = true;
      }
    }
  }
  return score;
}
bool cmp(pair<long long, long long> x, pair<long long, long long> y) {
  return (x.second - x.first) < (y.second - y.first);
}
