  long long lc = 0;
  for (long long i = 0; i < n; i++) lc += (s[i] == 'L');
  for (long long i = 0; i < n; i++) {
    if (s[i] == 'W')
      cnt++;
    else if (cnt != 0)
      a.push_back(cnt), cnt = 0;
  }
  if (cnt != 0) a.push_back(cnt);
  for (auto i : a) ans += i * 2 - 1;
  if (k == 0) {
    cout << ans << endl;
    return;
  }
  a.clear();
  cnt = 0;
  long long st = 0;
  while (s[st] == 'L' && st < n) st++;
  for (long long i = st; i < n; i++) {
    if (s[i] == 'L')
      cnt++;
    else if (cnt != 0)
      a.push_back(cnt), cnt = 0;
  }
  long long en = cnt;
  if (k >= lc) {
    cout << (2 * n) - 1 << endl;
  } else {
    if (k <= lc - st - en) {
      sort(a.begin(), a.end());
