def f(n, k , s):
    stack = []
    for ii in range(len(s)):
        if stack == [] or stack[-1][0] != s[ii]:
            stack.append([s[ii],1])
        else:
            stack[-1][1]+=1
    if len(stack) ==1:
        c = k if stack[0][0] == 'L' else n
        return max(0,c * 2 -1)

    score = 0
    for u in stack:
        if u[0] == 'W':
            score += (u[1] * 2) - 1 
    used = set([])
    potential = []
    for i,u in enumerate(stack):
        if 0<i<len(stack)-1 and u[0] == 'L':
            if k >= u[1]:
                potential.append((u[1],i))

    potential.sort()
    for p,i in potential:
        if k >= p:
            score += 2 * p +1
            used.add(i)
            k -= p


    for i,u in enumerate(stack):
        if u[0] == 'L' and i not in used:
            if k > 0:
                score += 2 * min(k,u[1])
                used.add(i)
                k-= min(k,u[1])
            else:
                break
    return score

