    static class BChessCheater {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int ntc = 1;
            ntc = in.nextInt();
            while ((ntc--) > 0) {
                int n = in.nextInt();
                int k = in.nextInt();
                char[] chrr = in.nextString().toCharArray();
                int first = -1;
                int last = -1;
                ArrayList<int[]> gaps = new ArrayList<>();
                int cntW = 0;
                for (int i = 0; i < n; i++) {
                    if (chrr[i] == 'W') {
                        if (first == -1) first = i;
                        if (last != -1) {
                            gaps.add(new int[]{last + 1, i - 1});
                        }
                        int j = i;
                        while (j < n && chrr[j] == 'W') {
                            last = j;
                            j++;
                            cntW++;
                        }
                        i = j - 1;
                    }
                }
                if (last == -1 || gaps.size() == 0) {
                    out.println(Math.max(0, (Math.min(k, n - cntW) + cntW) * 2 - 1));
                    continue;
                }
                Collections.sort(gaps, (a, b) -> {
                    return Math.abs(a[0] - a[1]) - Math.abs(b[0] - b[1]);
                });
                for (int[] gap : gaps) {
                    if (gap[1] - gap[0] + 1 <= k) {
                        for (int j = gap[0]; j <= gap[1]; j++) {
                            chrr[j] = 'W';
                            k--;
                        }
                    } else break;
                }
                for (int i = n - 2; i >= 0; i--) {
                    if (chrr[i + 1] == 'W' && chrr[i] == 'L' && k > 0) {
                        chrr[i] = 'W';
                        --k;
                    }
                }
                for (int i = 1; i < n; i++) {
                    if (chrr[i - 1] == 'W' && chrr[i] == 'L' && k > 0) {
                        chrr[i] = 'W';
                        k--;
                    }
                }
                out.println(countValue(chrr));
            }
        }

        public int countValue(char[] chrr) {
            char prev = 'L';
            int value = 0;
            for (char ch : chrr) {
                if (prev == 'L') {
                    if (ch == 'W') {
                        value += 1;
                    } else {
                        value += 0;
                    }
                } else {
                    if (ch == 'W') {
                        value += 2;
                    } else {
                        value += 0;
                    }
                }
                prev = ch;
            }
            return value;
        }

    }

