    public static int bfs(int s,ArrayList<Integer>[] a,int dist,boolean[] b,PrintWriter w){
        b[s]=true;
        int siz = 0;
        dist--;
        Queue<Integer> q = new LinkedList<>();
        q.add(s);
        while(q.size()!=0 && dist>0 ){
            int i=q.poll();
            Iterator<Integer> it = a[i].listIterator();
            int z=0;
            while(it.hasNext() && dist>0){
                z=it.next();
                if(!b[z]){
                    b[z]=true;
                    dist--;
                    //dist[z] = dist[i] + 1;
                    siz++;
                    q.add(z);
                }
            }   

        }
        return siz;
    }

    public static int lower(int key, int[] a){
        int l = 0;
        int r = a.length-1;
        int res = 0;

        while(l<=r){
            int mid = (l+r)/2;

            if(a[mid]<=key){
                l = mid+1;
                res = mid+1;
            }
            else{
                r = mid -1;
            }
        }

        return res;
    }




