        continue;
      }
      if (cur > 0) {
        holes.push_back(cur);
        tt += cur;
        cur = 0;
      }
    } else
      cur++;
  }
  tt += cur;
  sort(holes.begin(), holes.end());
  int ct = 0, dscore = 0;
  for (int i = 0; i < holes.size(); i++) {
    if (k >= holes[i]) {
      dscore += holes[i] * 2 + 1;
      k -= holes[i];
      tt -= holes[i];
    } else
      break;
  }
  dscore += 2 * min(k, tt);
  if (!start && dscore > 0) dscore--;
  return iscore + dscore;
}
