public class B
{
   public static void main(String[] args) throws IOException
   {
      FastScanner sc = new FastScanner();
      PrintWriter out = new PrintWriter(System.out);
      int T = sc.nextInt();
      for(int t = 0; t < T; t++){
         int n = sc.nextInt();
         int k = sc.nextInt();
         String str = sc.next();
         int ans = 0;
         PriorityQueue<Integer> pq = new PriorityQueue<Integer>();
         int leftover = 0;
         int cur = -1;
         for(int i = 0; i < n; i++){
            if(str.charAt(i) == 'W'){
               if(cur == -1){
                  leftover += i;
                  cur = i;
               }
               else{
                  if(i-cur-1 != 0) pq.add(i-cur-1);
                  cur = i;
               }
               if(i == 0){
                  ans++;
               }
               else{
                  if(str.charAt(i-1) == 'W'){
                     ans += 2;
                  }
                  else{
                     ans += 1;
                  }
               }
            }
         }
         leftover += n-cur-1;

         if(cur == -1){
            if(k > 0){
               out.println(2*(k-1)+1);
            }
            else{
               out.println(0);
            }
            continue;
         }
         while(k != 0 && !pq.isEmpty()){
            cur = pq.poll();
            if(k >= cur){
               ans += 2*(cur-1)+3;
               k -= cur;
            }
            else{
               ans += 2*k;
               k = 0;
            }
         }
         if(k != 0){
            ans += Math.min(k, leftover)*2;
         }
         out.println(ans);
      }
      out.close();
   }


   static class FastScanner {
      private int BS = 1 << 16;
      private char NC = (char) 0;
      private byte[] buf = new byte[BS];
      private int bId = 0, size = 0;
      private char c = NC;
      private double cnt = 1;
      private BufferedInputStream in;

      public FastScanner() {
         in = new BufferedInputStream(System.in, BS);
      }

      public FastScanner(String s) {
         try {
            in = new BufferedInputStream(new FileInputStream(new File(s)), BS);
         } catch (Exception e) {
            in = new BufferedInputStream(System.in, BS);
         }
      }

      private char getChar() {
         while (bId == size) {
            try {
               size = in.read(buf);
            } catch (Exception e) {
               return NC;
            }
            if (size == -1) 
               return NC;
            bId = 0;
         }
         return (char) buf[bId++];
      }

      public int nextInt() {
         return (int) nextLong();
      }

      public int[] nextInts(int N) {
         int[] res = new int[N];
         for (int i = 0; i < N; i++) {
            res[i] = (int) nextLong();
         }
         return res;
      }

      public long[] nextLongs(int N) {
         long[] res = new long[N];
         for (int i = 0; i < N; i++) {
            res[i] = nextLong();
         }
         return res;
      }

      public long nextLong() {
         cnt = 1;
         boolean neg = false;
         if (c == NC) c = getChar();
         for (; (c < '0' || c > '9'); c = getChar()) {
            if (c == '-') neg = true;
         }
         long res = 0;
         for (; c >= '0' && c <= '9'; c = getChar()) {
            res = (res << 3) + (res << 1) + c - '0';
            cnt *= 10;
         }
         return neg ? -res : res;
      }

      public double nextDouble() {
         double cur = nextLong();
         return c != '.' ? cur : cur + nextLong() / cnt;
      }

      public double[] nextDoubles(int N) {
         double[] res = new double[N];
         for (int i = 0; i < N; i++) {
            res[i] = nextDouble();
         }
         return res;
      }

      public String next() {
         StringBuilder res = new StringBuilder();
         while (c <= 32) c = getChar();
         while (c > 32) {
            res.append(c);
            c = getChar();
         }
         return res.toString();
      }

      public String nextLine() {
         StringBuilder res = new StringBuilder();
         while (c <= 32) c = getChar();
         while (c != '\n') {
            res.append(c);
            c = getChar();
         }
         return res.toString();
      }

      public boolean hasNext() {
         if (c > 32) 
            return true;
         while (true) {
            c = getChar();
            if (c == NC) 
               return false;
            else if (c > 32) 
               return true;
         }
      }
   }

   static void ASSERT(boolean assertion, String message) {
      if (!assertion) throw new AssertionError(message);
   }
}

