            if(s[i]=='W')
           {
               if(lcount!=0)
               nodes.add(lcount);
               lcount=0;
            wcount++;
           }
            else
            {
                lcount++;
            }
        }
        Collections.sort(nodes);
        for (int g=0;g<nodes.size();g++) {
            if(nodes.get(g)>k)
            {
                sub+=1;
                l+=nodes.get(g);
                continue;

            }
