def calculate(game):
    if not game:
        return 0
    su = 1 if game[0] == 'W' else 0
    prev = game[0]

    for g in game[1:]:
        if g == 'W':
            su += 2 if prev == 'W' else 1
            prev = 'W'
        else:
            prev = 'L'

    return su


