            if top > k:
                bonus += k*2
                k = 0
            else:
                bonus += top*2
                k -= top

        else:
            top = gaps.pop(0)

            if top > k:
                bonus += k*2
                k = 0
            else:
                bonus += top*2 + 1
                k -= top

    print(calculate(games) + bonus)

