const int maxn = 2e5 + 7;
const int mod = 1e9 + 7;
const long long INF = 1e18;
vector<int> a;
char s[maxn];
void rua() {
  int n, m, k = 0, ans = 0;
  scanf("%d%d", &n, &m);
  a.clear();
  scanf("%s", s + 1);
  for (int i = 1; i <= n; i++)
    if (s[i] == 'L') k++;
  m = min(m, k);
  int ol = 0, ow = 0, ff = 0;
  for (int i = 1; i <= n; i++) {
    if (s[i] == 'L') {
      ol++;
      if (s[i - 1] == 'W') {
        ans += 2 * ow - 1;
        ow = 0;
      }
    } else {
      ow++;
      if (s[i - 1] == 'L') {
        if (ff) a.push_back(ol);
        ol = 0;
      }
      ff = 1;
    }
  }
  if (ow) ans += 2 * ow - 1;
  sort(a.begin(), a.end());
  for (auto x : a) {
    if (m < x) {
      ans += 2 * m;
      m = 0;
      break;
    }
    ans += 2 * x + 1;
    m -= x;
  }
  if (m) {
    ans += 2 * m;
    if (!ff) ans--;
  }
  printf("%d\n", ans);
}
