public class B_1427 {

    public static void main(String[] args) throws IOException {
        FastIO in = new FastIO(args);
        int t = in.ni();
        while (t-- > 0) {
            int n = in.ni();
            int k = in.ni();
            char c[] = in.next().toCharArray();
            int start = -1;
            int ending = n - 1;
            for (int i = 0; i < n; i++) {
                if (c[i] == 'W') {
                    start = i;
                    break;
                }
            }
            for (int i = n - 1; i >= 0; i--) {
                if (c[i] == 'W') {
                    ending = i;
                    break;
                }
            }
            if (start == -1) {
                if (k == 0)
                    System.out.println(0);
                else
                    System.out.println(2 * (k - 1) + 1);
            } else {
                int end = 0;
                int count = 0;
                LinkedList<int[]> l = new LinkedList<>();
                for (int i = start; i <= ending; i++) {
                    if (c[i] == 'W') {
                        if (count != 0) {
                            l.add(new int[] { end, count });
                        }
                        count = 0;
                    } else {
                        end = i;
                        count++;
                    }
                }
                if (count != 0) {
                    l.add(new int[] { end, count });
                }
                Collections.sort(l, new Comparator<int[]>() {
                    @Override
                    public int compare(int[] a, int[] b) {
                        if (a[1] == b[1]) {
                            return Integer.compare(a[0], b[0]);
                        }
                        return Integer.compare(a[1], b[1]);
                    }
                });
                int ans = 0;
                for (int i = 0; i < n; i++) {
                    if (c[i] == 'W') {
                        if (i != 0) {
                            if (c[i - 1] == 'W')
                                ans += 2;
                            else
                                ans += 1;
                        } else
                            ans += 1;
                    }
                }
                while (k > 0 && !l.isEmpty()) {
                    int[] set = l.remove();
                    // System.out.println(set[0]);
                    if (set[1] <= k) {
                        ans += set[1] * 2 + (set[0] == n - 1 ? 0 : 1);
                        k -= set[1];
                    } else if (k > 0) {
                        ans += k * 2;
                        k = 0;
                    }
                }
                int r = Math.min(k, n - ending - 1);
                ans += 2 * r;
                k -= r;
                ans += Math.min(k, start) * 2;
                System.out.println(ans);
            }
        }
        in.bw.flush();
    }

    static class Data implements Comparable<Data> {

        int a, b;

        public Data(int a, int b) {
            this.a = a;
            this.b = b;
        }

        @Override
        public int compareTo(Data o) {
            if (a == o.a) {
                return Integer.compare(b, o.b);
            }
            return Integer.compare(a, o.a);
        }

        public static void sort(int a[]) {
            Data d[] = new Data[a.length];
            for (int i = 0; i < a.length; i++) {
                d[i] = new Data(a[i], 0);
            }
            Arrays.sort(d);
            for (int i = 0; i < a.length; i++) {
                a[i] = d[i].a;
            }
        }
    }

    static class FastIO {

        private final BufferedReader br;
