import java.util.*;
import java.util.stream.Collectors;

public class B1427 {
    private final static Scanner scanner = new Scanner(System.in);

    private static int size;
    private static int times;

    public static void main(String[] args) {
        int groups = scanner.nextInt();
        for (int i = 0; i < groups; i++) {
            inner();
        }
    }

    private static void inner(){
        size = scanner.nextInt();
        times = scanner.nextInt();
        String result = scanner.next();
        char[] game = new char[size];
        for (int i = 0; i < size; i++) {
            game[i] = result.charAt(i);
        }
        int lostNum = result.replace("W", "").length();
        if (times == 0){

        }else if (lostNum <= times){
            for (int i = 0; i < size; i++) {
                game[i] = 'W';
            }
        }else{
            math(game, result);
        }

        //输出
        int count = 0;
        for (int i = size-1; i > 0; i--) {
            if (game[i] == 'W' && game[i-1] == 'W'){
                count += 2;
            }else if (game[i] == 'W' && game[i-1] == 'L'){
                count += 1;
            }
        }
        if (game[0] == 'W'){
            count += 1;
        }
//        for (int i = 0; i < size; i++) {
//            System.out.print(game[i]);
//        }
        System.out.println(count);
    }

    private static void math(char[] game, String string){
        //看是否有WLL..n..LLW的存在，若有则获取
        int distence;
        List<Flag>  flags = new ArrayList<>();
        for (int  a = 0; a < size-1; a++) {
            if (game[a] == 'W' && game[a+1]== 'L'){
                distence = 0;
                for (int i = a+1; i < size-1; i++) {
                    if (game[i] == 'L' && game[i+1] == 'L'){
                        distence += 1;
                    }else if (game[i] == 'L' && game[i+1] == 'W'){
                        distence += 1;
                        flags.add(new Flag(a+1, distence));
                        //从数组当前位置继续遍历,降低复杂度
                        a = i;
                        break;
                    }
                }
            }
        }
        flags = flags.stream().sorted(Comparator.comparing(Flag::getDistence)).collect(Collectors.toList());
        //运算
        if (!flags.isEmpty()){
            for (Flag flag:flags){
                for (int i = flag.getPoint(); i < flag.getPoint()+flag.getDistence(); i++) {
                    if (game[i] == 'L') {
                        game[i] = 'W';
                        if (--times == 0) return;
                    }
                }
            }
        }
        //给形如LLLWWWLLL中的前半部改值
        int fountPoint = -1;
        for (int i = 0; i < size-1; i++) {
            if (game[i] == 'L' && game[i+1] == 'W'){
                fountPoint = i;
                break;
            }
        }
        if (fountPoint != -1){
            for (int i = fountPoint; i >=0 ; i--) {
                if (game[i] == 'L'){
                    game[i] = 'W';
                    if (--times == 0) return;
                }
            }
        }
        //给形如LLLWWWLLL中的后半部改值
        int backPoint = -1;
        for (int i = 1; i < size; i++) {
            if (game[i] == 'L' && game[i-1] == 'W'){
                backPoint = i;
                break;
            }
        }
        if (backPoint != -1){
            for (int i = backPoint; i <size ; i++) {
                if (game[i] == 'L'){
                    game[i] = 'W';
                    if (--times == 0) return;
                }
            }
        }
        //给形如LLLLLLLLL挨个改值
        for (int i = 0; i < size; i++) {
            if (game[i] == 'L'){
                game[i] = 'W';
                if (--times == 0) return;
            }
        }
    }

}

class Flag {
    private int point;
    private int distence;

    public Flag(int point , int distence) {
        this.point = point;
        this.distence = distence;
    }

    public int getPoint() {
        return point;
    }
    public void setPoint(int point) {
        this.point = point;
    }
    public int getDistence() {
        return distence;
    }
    public void setDistence(int distence) {
        this.distence = distence;
    }
    @Override
    public String toString() {
        return "Flags{" +
                "point=" + point +
                ", distence=" + distence +
                '}';
