import java.util.ArrayList;
import java.util.Collections;
import java.util.Scanner;

public class CodeForces {

    public static int n;
    public static int k;
    public static int score;
    public static String range;
    public static int INF = 1000000000;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int match = scanner.nextInt();
        for (int t = 0; t < match; t++) {
            n = scanner.nextInt();
            k = scanner.nextInt();
            scanner.nextLine();
            String s = scanner.nextLine();
            range = s;
            score = 0;
            char[] a = range.toCharArray();
            findMaxScore(a);
        }
    }

    public static void findMaxScore(char[] a) {
        int losses = 0;
        int wins = 0;
        int wins_streaks = 0;
        ArrayList<Integer> losses_streaks = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if (a[i] == 'W') {
                wins += 1;
                if (i == 0 || a[i - 1] == 'L') {
                    wins_streaks += 1;
                }
            } else {
                losses += 1;
                if (i == 0 || a[i - 1] == 'W') {
                    losses_streaks.add(0);
                }
                losses_streaks.set(losses_streaks.size() - 1, losses_streaks.get(losses_streaks.size() - 1) + 1);
            }
        }

        if (wins == 0) {
            if (k == 0) {
                System.out.println(0);
                return;
            }
            System.out.println(2 * k - 1);
            return;
        }
        if (k >= losses) {
            System.out.println(2 * n - 1);
            return;
        }
        wins += k;
        if (a[0] == 'L') {
            losses_streaks.set(0, INF);
        }
        if (a[n - 1] == 'L') {
            losses_streaks.set(losses_streaks.size() - 1, INF);
        }
        Collections.sort(losses_streaks);
        for (Integer streak : losses_streaks) {
            if (streak <= k) {
                k -= streak;
                wins_streaks -= 1;
            } else {
                break;
            }
        }
        System.out.println(2 * wins - wins_streaks);
    }
}

