def solve(record, length, changes):
    if record == 'L' * length:
        return changes * 2 - 1 if changes > 0 else 0
    state = 'N'
    loss_runs = []
    win_runs = []
    counter = 0
    bonus = 0
    score = 0
    for i in record:
        if state == 'N':
            if i == 'L':
                bonus += 1
            else:
                state = 'W'
                counter += 1
        else:
            if state == 'W':
                if i == 'W':
                    counter += 1
                else:
                    win_runs.append(counter)
                    counter = 1
                    state = 'L'
            else:
                if i == 'L':
                    counter += 1
                else:
                    loss_runs.append(counter)
                    counter = 1
                    state = 'W'
    if state == 'W':
        win_runs.append(counter)
    else:
        bonus += counter

    for i in win_runs:
        score += 2*i-1

    loss_runs = sorted(loss_runs)
    for i in loss_runs:
        if changes >= i:
            score += 2*i+1
            changes -= i
        else:
            score += 2*changes
            changes = 0
    if changes != 0:
        score += min(bonus*2, changes*2)
    return score




