using namespace std;
void solve() {
  int n, k;
  cin >> n >> k;
  string s;
  cin >> s;
  vector<int> blocks;
  for (int i = 0; i < n; ++i) {
    bool good_start = (i > 0);
    if (s[i] == 'W') {
      continue;
    }
    int len = 0;
    while (i < n && s[i] == 'L') {
      len++;
      i++;
    }
    if (good_start && i != n) {
      blocks.push_back(len);
    }
  }
  sort(blocks.begin(), blocks.end());
  int answer = 0;
  int remaining = 0;
  for (int i = 0; i < n; ++i) {
    if (s[i] == 'W') {
      answer++;
    } else {
      remaining++;
    }
    if (s[i] == 'W' && i > 0 && s[i - 1] == 'W') answer++;
  }
  if (remaining == n && k > 0) {
    answer--;
  }
  for (int x : blocks) {
    if (k >= x) {
      k -= x;
      answer += 2 * x + 1;
      remaining -= x;
    } else {
      break;
    }
  }
  answer += 2 * min(k, remaining);
  cout << answer << endl;
}
int main(int argc, char const *argv[]) {
  int T;
  cin >> T;
  for (int t = 0; t < T; t++) solve();
  return 0;
}

