    public static void mergeSort(ArrayList<Integer> a[], int p, int r)
    {
        if(p<r)
        {
            int q=(p+r)/2;
            mergeSort(a,p,q);
            mergeSort(a,q+1,r);
            merge(a,p,q,r);
        }
    }
    public static void merge(ArrayList<Integer> a[],int p,int q,int r)
    {
        int n1=q-p+1;
        ArrayList<Integer> L[]=new ArrayList[n1];
        int n2=r-q;
        ArrayList<Integer> R[]=new ArrayList[n2];
        for(int i=p;i<=q;i++)
        {
            L[i-p]=a[i];
        }
        for(int i=q+1;i<=r;i++)
        {
            R[i-q-1]=a[i];
        }
        int x=0,y=0;
        int k=p;
        while(x<n1 && y<n2)
        {
            int dl=L[x].get(1)-L[x].get(0)+1;
            int dr=R[y].get(1)-R[y].get(0)+1;
            if(dl<=dr)
            {
                a[k]=L[x];
                x++;
            }
            else
            {
                a[k]=R[y];
                y++;
            }
            k++;
        }
        while(x<n1)
        {
            a[k]=L[x];
            x++;
            k++;
        }
        while(y<n2)
        {
            a[k]=R[y];
            k++;
            y++;
        }

    }
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new
                    InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }
}

