void solve() {
  int n, k;
  string s;
  cin >> n >> k >> s;
  int lCnt = 0;
  for (char c : s)
    if (c == 'L') lCnt++;
  if (lCnt == n) {
    cout << max(0, 2 * (k - 1) + 1) << '\n';
    return;
  }
  int start = 0;
  while (s[start] == 'L') start++;
  int end = n - 1;
  while (s[end] == 'L') end--;
  vector<pair<int, int>> cnts;
  for (int i = start; i <= end;) {
    while (s[i] == 'W') i++;
    int cnt = 0;
    while (i + cnt <= end && s[i + cnt] == 'L') cnt++;
    if (cnt) cnts.emplace_back(cnt, i);
    i += cnt;
  }
  sort((cnts).begin(), (cnts).end());
  for (auto [amt, ind] : cnts) {
    for (auto i = (ind); (i) < (ind + min(amt, k)); ++(i)) s[i] = 'W';
    k -= min(amt, k);
  }
  int k2 = k;
  for (auto i = (end + 1); (i) < (min(n, end + 1 + k2)); ++(i)) {
    k--;
    s[i] = 'W';
  }
  for (auto i = (0); (i) < ((min(k, start))); ++(i)) s[start - 1 - i] = 'W';
  long long score = 0;
  for (int i = 0; i < n;) {
    if (s[i] == 'L') {
      i++;
    } else {
      score++;
      i++;
      while (i < n && s[i] == 'W') {
        score += 2;
        i++;
      }
    }
  }
  cout << score << '\n';
}
int main() {
  cin.tie(0);
  ios_base::sync_with_stdio(0);
  int tc = 1;
  cin >> tc;
  for (auto i = (1); (i) < (tc + 1); ++(i)) {
    solve();
  }
}

