                pairs[pi]=new pair(wi.get(i-1),wi.get(i));
                pi++;
            }
            Arrays.sort(pairs);
            for(int i=0;i<pairs.length&&k>0;i++){
                pair cur=pairs[i];
                for(int j=cur.li;j<cur.wi&&k>0;j++){
                    ss.setCharAt(j,'W');
                    k--;
                }
            }
            if(k>0){
                if(wi.size()>0&&wi.size()%2==0){
                    int li=wi.get(wi.size()-1);
                    for(int j=li;j<n&&k>0;j++){
                        ss.setCharAt(j,'W');
                        k--;
                    }
                }
            }
            if(k>0){
                int fi=n;
                if(wi.size()>0){
                    fi=wi.get(0);
                }
                for(int j=fi-1;j>=0&&k>0;j--){
                    ss.setCharAt(j,'W');
                    k--;
                }
            }
            int ans=0;
            if(ss.charAt(0)=='W'){
                ans=1;
            }
            for(int i=1;i<n;i++){
                if(ss.charAt(i)=='W'){
                    ans++;
                    if(ss.charAt(i-1)=='W'){
                        ans++;
                    }
                }
            }
            out.println(ans);
        }
        out.flush();
        out.close();
    }
    static class pair implements Comparable<pair>{
        int li;
        int wi;
        pair(int li,int wi){
            this.li=li;
            this.wi=wi;
        }

        @Override
        public int compareTo(pair o) {
            int l1=this.wi-this.li;
            int l2=o.wi-o.li;
            return l1-l2;
        }
    }
    static class FastReader {

        byte[] buf = new byte[2048];
        int index, total;
        InputStream in;

        FastReader(InputStream is) {
            in = is;
        }

        int scan() throws IOException {
            if (index >= total) {
                index = 0;
                total = in.read(buf);
                if (total <= 0) {
                    return -1;
                }
            }
            return buf[index++];
        }

        String next() throws IOException {
            int c;
            for (c = scan(); c <= 32; c = scan()) ;
            StringBuilder sb = new StringBuilder();
            for (; c > 32; c = scan()) {
                sb.append((char) c);
            }
            return sb.toString();
        }

        int nextInt() throws IOException {
            int c, val = 0;
            for (c = scan(); c <= 32; c = scan()) ;
            boolean neg = c == '-';
            if (c == '-' || c == '+') {
                c = scan();
            }
            for (; c >= '0' && c <= '9'; c = scan()) {
                val = (val << 3) + (val << 1) + (c & 15);
            }
            return neg ? -val : val;
        }
        long nextLong() throws IOException {
            int c;
            long val = 0;
            for (c = scan(); c <= 32; c = scan()) ;
            boolean neg = c == '-';
            if (c == '-' || c == '+') {
                c = scan();
            }
            for (; c >= '0' && c <= '9'; c = scan()) {
                val = (val << 3) + (val << 1) + (c & 15);
            }
            return neg ? -val : val;
        }
    }
}

