def f(n, k, s):
    wins = loss = 0
    win_streak = 0
    lose_streaks = []
    for i in range(n):
        if s[i] == 'W':
            wins += 1
            if i == 0 or s[i - 1] == 'L':
                win_streak += 1
        else:
            loss += 1
            if i == 0 or s[i - 1] == 'W':
                lose_streaks.append(0)
            lose_streaks[-1] += 1
    if k >= loss:
        return 2 * n - 1
    if wins == 0:
        if k == 0:
            return 0
        return 2 * k - 1

    if s[0] == 'L':
        lose_streaks[0] = 1e9
    if s[-1] == 'L':
        lose_streaks[-1] = 1e9
    wins += k
    lose_streaks.sort()
    for i in lose_streaks:
        if i > k:
            break
        k -= i
        win_streak -= 1
    return 2 * wins - win_streak


