                    score+=1
                win += 1
                dicLis.append(curr)
                curr=0
            else:
                win = 0
                curr+=1
        dicLis.append(curr)

        for i in range(1,len(dicLis)-1):
            if dicLis[i]:
                dic[dicLis[i]]+=1
        itemRino = sorted(list(dic.items()))

        for ele in itemRino:
            gap, count = ele
            while count:

                if k>=gap:
                    score += gap*2+1
                else:
                    score += k*2
                count-=1
                k -= gap
                #print(gap,k,score)
                if  k<=0:
                    break
            if k<=0:
                break
        if k>0:
