        if (s[j - 1] == 'W') indx = j;
        ++cnt;
      } else {
        p = j;
        if (cnt) v.push_back(make_pair(cnt, indx));
        cnt = 0;
      }
    }
    sort(v.begin(), v.end());
    for (i = 0; i < v.size(); ++i) {
      for (j = v[i].second; j < v[i].first + v[i].second && k > 0; ++j) {
        s[j] = 'W';
        --k;
      }
    }
  next:
    if (k) {
      for (i = m - 1; i >= 0 && k > 0; --i) {
        s[i] = 'W';
        --k;
      }
    }
    if (k) {
      for (i = p + 1; i < n && k > 0; ++i) {
        s[i] = 'W';
