int score(string &s, int &n) {
  int ans = 0;
  for (int i = 0; i < n; i++) {
    if (s[i] == 'W') {
      if (!i || s[i - 1] != 'W') {
        ans++;
      } else {
        ans += 2;
      }
    }
  }
  return ans;
}
void solve() {
  int n, k;
  cin >> n >> k;
  string s;
  cin >> s;
  if (!k) {
    cout << score(s, n) << "\n";
    return;
  }
  vector<int> pos;
  for (int i = 0; i < n; i++) {
    if (s[i] == 'W') {
      pos.push_back(i);
    }
  }
  if (pos.empty()) {
    cout << min(2 * k - 1, 2 * n - 1) << "\n";
    return;
  }
  if ((int)pos.size() + k >= n) {
    cout << 2 * n - 1 << "\n";
    return;
  }
  if (pos.size() == 1) {
    cout << min(2 * (k + 1) - 1, 2 * n - 1) << "\n";
    return;
  }
  vector<vector<int> > v;
  for (int i = 1; i < pos.size(); i++) {
    if (pos[i] - pos[i - 1] > 1) {
      vector<int> a = {pos[i] - pos[i - 1] - 1, pos[i - 1], pos[i]};
      v.push_back(a);
    }
  }
  sort(v.begin(), v.end());
  for (int i = 0; i < v.size(); i++) {
    if (k >= v[i][0]) {
      k -= v[i][0];
      for (int j = v[i][1] + 1; j < v[i][2]; j++) {
        s[j] = 'W';
      }
    } else {
      int j = v[i][1] + 1;
      while (k--) {
        s[j] = 'W';
        j++;
      }
      cout << score(s, n) << "\n";
      return;
    }
  }
  if (k) {
    for (int i = pos.back() + 1; i < n; i++) {
      if (k) {
        s[i] = 'W';
        k--;
      } else {
        cout << score(s, n) << "\n";
        return;
      }
    }
  }
  if (k) {
    for (int i = pos.front() - 1; i >= 0; i--) {
      if (k) {
        s[i] = 'W';
        k--;
      } else {
        cout << score(s, n) << "\n";
        return;
      }
    }
  }
  cout << score(s, n) << "\n";
  return;
}
