                sb.append(0).append("\n"); continue;
            }
            int prevw=j;
            for (int k = j+1; k < N; k++) {//Compute current value
                if(s.charAt(k)=='W'){

                    cur++; 
                    if(s.charAt(k-1)=='W'){
                        cur++;
                    }else{
                        arl.add(k-prevw-1);
                    }
                    prevw=k;
                }
            }

            int r=N-1-prevw;// j and r are the lengths of the side intervals
            Collections.sort(arl);
            boolean done=false;
            int c;
            for (int k = 0; k < arl.size(); k++) {
                c=arl.get(k);
                if(K>=c){
                    K-=c; cur+=(2*c+1);
                    if(K==0){
                        done=true;
                        sb.append(cur).append("\n"); break;
                    }
                }else{
                    cur+=2*K; K=0;
                    done=true;
                    sb.append(cur).append("\n");
                    break;
                }
            }
            if(K>0){
                done=true;
                cur+=2*Math.min(K,j+r);
                sb.append(cur).append("\n");
            }else if(!done){//All wins
                sb.append(cur).append("\n");
            }

        }
        System.out.println(sb.toString());

    }
    //Special Cases: n=1, etc
    //Bounds: Overflow, ?
}

