public class B {
    private static class Solver {
        private void solve() throws Exception {
            int cases = in.nextInt();
            while (cases-- > 0) {
                out.println(compute(in.nextInt(), in.nextInt(), in.next()));
            }
        }

        private int compute(int n, int k, String s) {
            int pre = -1;
            int suf = n;

            for (int i = 0; i < n; i++) {
                if (s.charAt(i) == 'W') {
                    pre = i;
                    break;
                }
            }
            if (pre == -1) {
                return Math.max(0, k * 2 - 1);
            }
            for (int i = n - 1; i >= 0; i--) {
                if (s.charAt(i) == 'W') {
                    suf = i;
                    break;
                }
            }
            List<Integer> p = new ArrayList<>(n);
            int snippet = 0;
            int totalW = 0;
            for (int i = pre; i <= suf; ) {
                while (i <= suf && s.charAt(i) == 'W') {
                    totalW++;
                    i++;
                }
                int partLen = 0;
                while (i <= suf && s.charAt(i) == 'L') {
                    partLen++;
                    i++;
                }
                if (partLen > 0) {
                    p.add(partLen);
                }
            }
            if (n - totalW <= k) {
                return n * 2 - 1;
            }

            Collections.sort(p);

            int totalPart = p.size() + 1;
            int leftK = k;
            for (int x : p) {
                if (x <= leftK) {
                    leftK -= x;
                    totalW += x;
                    totalPart--;
                }
            }
            totalW += leftK;
            return totalW * 2 - totalPart;
        }
    }

    //--------------------------------------------------------
    private static final MyScanner in = new MyScanner(System.in);
    private static final PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));

    public static void main(String[] args) throws Exception {
        Solver solver = new Solver();
        solver.solve();
        out.close();
    }

